import { NextApiRequest, NextApiResponse } from "next";
import neo4j, { Driver, Session, Result, Record } from "neo4j-driver";
import { log } from "console";

const driver = neo4j.driver(
  "bolt://44.201.240.207:7687",
  neo4j.auth.basic("neo4j", "job-returns-machines")
);

// Define the type for properties
type Dict&lt;T extends PropertyKey, U&gt; = {
  [K in T]: U;
};

// API route handler
export default async function handler(
  req: NextApiRequest,
  res: NextApiResponse
) {
  const session: Session = driver.session();

  try {
    // Perform your Neo4j queries and logic here
    const result = await session.run("MATCH (n) RETURN n LIMIT 5");

    // const nodes: Record&lt;string, any&gt;[] = result.records.map(record =&gt; record.get('n').properties);
    const nodes: Dict&lt;PropertyKey, any&gt;[] = result.records.map(record =&gt; record.get('n').properties);

    console.log(nodes);

    const jsonData = JSON.stringify(nodes);

    res.status(200).json({ data: jsonData });

  } catch (error) {
    console.error("Error executing Neo4j query:", error);
    res.status(500).json({ error: "Internal Server Error" });
  } finally {
    session.close();
  }
}

