The Providers component used for wrapping components as a parent component that child components can import states and hooks just by importing (not as a component props).
"use client";
import { Provider } from "react-redux";
import { store } from "./store";

export default function Providers({ children }: { children: React.ReactNode }) {
  return &lt;Provider store={store}&gt;{children}&lt;/Provider&gt;;
}

